"use client";

import {
  FileText,
  FolderOpen,
  Settings,
  Search,
  Copy,
  Scissors,
  Eye,
  Download,
  Upload,
  Users,
  Bell,
  Trash2,
  type LucideIcon,
} from "lucide-react";

import {
  Combobox,
  ComboboxCollection,
  ComboboxEmpty,
  ComboboxGroup,
  ComboboxGroupLabel,
  ComboboxInput,
  ComboboxItem,
  ComboboxList,
  ComboboxPopup,
  ComboboxSeparator,
} from "@/registry/pure-ui/ui/combobox";
import { Fragment, useState } from "react";

type Command = {
  id: string;
  label: string;
  description: string;
  icon: LucideIcon;
  shortcut?: string;
  category: "File" | "Edit" | "View" | "Tools" | "Help";
};

type CommandGroup = {
  value: string;
  items: Command[];
};

const commandsData: Command[] = [
  // File commands
  {
    id: "file-new",
    label: "New File",
    description: "Create a new file",
    icon: FileText,
    shortcut: "Ctrl+N",
    category: "File",
  },
  {
    id: "file-open",
    label: "Open File",
    description: "Open an existing file",
    icon: FolderOpen,
    shortcut: "Ctrl+O",
    category: "File",
  },
  {
    id: "file-save",
    label: "Save",
    description: "Save current file",
    icon: Download,
    shortcut: "Ctrl+S",
    category: "File",
  },
  {
    id: "file-export",
    label: "Export",
    description: "Export current project",
    icon: Upload,
    shortcut: "Ctrl+E",
    category: "File",
  },

  // Edit commands
  {
    id: "edit-copy",
    label: "Copy",
    description: "Copy selected text",
    icon: Copy,
    shortcut: "Ctrl+C",
    category: "Edit",
  },
  {
    id: "edit-cut",
    label: "Cut",
    description: "Cut selected text",
    icon: Scissors,
    shortcut: "Ctrl+X",
    category: "Edit",
  },
  {
    id: "edit-find",
    label: "Find",
    description: "Search in current file",
    icon: Search,
    shortcut: "Ctrl+F",
    category: "Edit",
  },
  {
    id: "edit-delete",
    label: "Delete",
    description: "Delete selected item",
    icon: Trash2,
    shortcut: "Del",
    category: "Edit",
  },

  // View commands
  {
    id: "view-preview",
    label: "Toggle Preview",
    description: "Show or hide preview panel",
    icon: Eye,
    shortcut: "Ctrl+Shift+V",
    category: "View",
  },
  {
    id: "view-sidebar",
    label: "Toggle Sidebar",
    description: "Show or hide sidebar",
    icon: FolderOpen,
    shortcut: "Ctrl+B",
    category: "View",
  },

  // Tools commands
  {
    id: "tools-settings",
    label: "Settings",
    description: "Open settings",
    icon: Settings,
    shortcut: "Ctrl+,",
    category: "Tools",
  },
  {
    id: "tools-notifications",
    label: "Notifications",
    description: "View all notifications",
    icon: Bell,
    category: "Tools",
  },
  {
    id: "tools-users",
    label: "User Management",
    description: "Manage users and permissions",
    icon: Users,
    category: "Tools",
  },

  // Help commands
  {
    id: "help-docs",
    label: "Documentation",
    description: "Open documentation",
    icon: FileText,
    category: "Help",
  },
  {
    id: "help-search",
    label: "Search Help",
    description: "Search help articles",
    icon: Search,
    shortcut: "F1",
    category: "Help",
  },
];

function groupCommands(commands: Command[]): CommandGroup[] {
  const groups: Record<string, Command[]> = {};
  for (const command of commands) {
    if (!groups[command.category]) {
      groups[command.category] = [];
    }
    // biome-ignore lint/style/noNonNullAssertion: will never be null
    groups[command.category]!.push(command);
  }

  const order: Array<CommandGroup["value"]> = [
    "File",
    "Edit",
    "View",
    "Tools",
    "Help",
  ];
  return order.map((value) => ({ items: groups[value] ?? [], value }));
}

const groupedCommands: CommandGroup[] = groupCommands(commandsData);

export function ComboboxGroupingItemsDemo() {
  const [selectedCommand, setSelectedCommand] = useState<Command | null>(null);

  return (
    <div className="flex flex-col gap-4">
      <Combobox
        items={groupedCommands}
        value={selectedCommand}
        onValueChange={(command: Command | null) => {
          setSelectedCommand(command);
        }}
        itemToStringLabel={(command: Command) => command.label}
      >
        <div className="flex flex-col items-start gap-2">
          <ComboboxInput
            aria-label="Search commands"
            placeholder="Type a command or search..."
            className="w-full"
          />
        </div>
        <ComboboxPopup>
          <ComboboxEmpty>No commands found.</ComboboxEmpty>
          <ComboboxList>
            {(group: CommandGroup) => (
              <Fragment key={group.value}>
                <ComboboxGroup items={group.items}>
                  <ComboboxGroupLabel>{group.value}</ComboboxGroupLabel>
                  <ComboboxCollection>
                    {(command: Command) => {
                      const Icon = command.icon;
                      return (
                        <ComboboxItem key={command.id} value={command}>
                          <div className="flex items-center justify-between gap-3 w-full">
                            <div className="flex items-center gap-3 min-w-0 flex-1">
                              <Icon className="h-4 w-4 shrink-0 opacity-70" />
                              <div className="flex flex-col min-w-0">
                                <span className="font-medium truncate">
                                  {command.label}
                                </span>
                                <span className="text-xs opacity-60 truncate">
                                  {command.description}
                                </span>
                              </div>
                            </div>
                            {command.shortcut && (
                              <kbd className="hidden sm:inline-flex h-5 shrink-0 items-center gap-1 rounded border bg-muted px-1.5 font-mono text-[10px] font-medium opacity-60">
                                {command.shortcut}
                              </kbd>
                            )}
                          </div>
                        </ComboboxItem>
                      );
                    }}
                  </ComboboxCollection>
                </ComboboxGroup>
                {group.value !== "Help" && <ComboboxSeparator />}
              </Fragment>
            )}
          </ComboboxList>
        </ComboboxPopup>
      </Combobox>

      {selectedCommand && (
        <div className="rounded-lg border bg-muted/50 p-4">
          <p className="text-sm text-muted-foreground">
            Selected:{" "}
            <strong className="text-foreground">{selectedCommand.label}</strong>
            {selectedCommand.shortcut && (
              <span className="ml-2 text-xs opacity-60">
                ({selectedCommand.shortcut})
              </span>
            )}
          </p>
        </div>
      )}
    </div>
  );
}
